import React, { useEffect, useRef, useState } from "react";


const Ref = () => {
    const [input, setInput] = useState("");
    const count = useRef(input);

    useEffect(() => {
        count.current = input;
    });

    return (
        <React.Fragment>
            <input 
                type="text"
                value={input}
                onChange={ (a) => setInput(a.target.value)}
            />
            <h1>count is: {count.current}</h1>
        </React.Fragment>
    );
}

export default Ref;
