import React, { useEffect, useState } from "react";

const Timer = () => {
    const [time, setTime] = useState(0);

    useEffect(() => {
        const timer = setTimeout(() => {
            setTime(() => time +1);
        }, 1000);

        return () => clearTimeout(timer);
    }, []);

    return <h1>Rendered {time} times!</h1>;
}

export default Timer;
