};

const Todos = () => {
    const [todos, dispatchToReducer] = useReducer(todoReducer, initialTodos);

    const handleComplete = (todo) => {
        dispatchToReducer({ type: "COMPLETE", id: todo.id });
    };

    return (
        <React.Fragment>
            {todos.map((todo) => (
                <div key={todo.id}>
                    <label>
                        <input
                            type="checkbox"
                            checked={todo.complete}
                            onChange={() => handleComplete(todo)}
                        />
                        {todo.title}
                    </label>
                </div>
            ))}
        </React.Fragment>
    );
}

export default Todos;
