const initialTodos = [
    {
        id: 1,
        title: "Todo 1",
        complete: false,
    },
    {
        id: 2,
        title: "Todo 2",
        complete: false,
    }
];

const reducer = (state, action) => {
    switch (action.type) {
        case "COMPLETE":
